import React from 'react';
import './Catalogs.scss';
import { NavLink } from 'react-router-dom';
// import { getPalettes } from '../../util/apiCalls';
import Fade from 'react-reveal/Fade';

const Catalogs = ({
	catalogs,
	menuIsActive,
	updateCurrentCatalog,
	removeCatalog,
	fetchCatalogs
}) => {
	let allCatalogs;

	const handleDelete = async catalog => {
		await removeCatalog(catalog);
		await fetchCatalogs();
	};

	if (catalogs.length) {
		allCatalogs = catalogs.map(catalog => {
			const { catalogName, id } = catalog;
			return (
				<NavLink
					onClick={() => updateCurrentCatalog(id)}
					key={id}
					exact
					to={`/catalogs/${id}`}
					activeClassName='active-catalog'>
					<div key={id}>
						<button onClick={() => handleDelete(catalog)}>x</button>{' '}
						<li>{catalogName}</li>
					</div>
				</NavLink>
			);
		});
	}

	return (
		<section className='Catalogs'>
			<h2>Catalogs</h2>
			<ul>
				<Fade when={menuIsActive} delay={200} duration={400} bottom>
					{allCatalogs}
				</Fade>
			</ul>
		</section>
	);
};

export default Catalogs;
