import React, { Component } from 'react';
import './App.scss';
import NavBar from '../NavBar/NavBar';
import Footer from '../Footer/Footer';
import LoginForm from '../LoginForm/LoginForm.js';

class App extends Component {
	constructor() {
		super();
		this.state = {
			arrayOfColors: [],
			userName: '',
			userId: 0,
			catalogs: [],
			palettes: []
		};
	}

	saveCatalogsAndPalettes = (catalogs, palettes) => {
		this.setState({
			catalogs,
			palettes
		});
	};

	updateCurrentUser = (user, catalogs, palettes) => {
		const { firstName, id } = user;
		this.setState({ userName: firstName, userId: id, catalogs, palettes });
	};

	render() {
		return (
			<div className='App'>
				<NavBar />
				<Footer />
				<LoginForm updateCurrentUser={this.updateCurrentUser} />
			</div>
		);
	}
}

export default App;
