import React, { useState } from 'react';
import { userLogin, getCatalogs, getPalettes } from '../../util/apiCalls.js';
import './LoginForm.scss';

const LoginForm = ({ updateCurrentUser }) => {
	const [emailValue, handleEmailChange] = useState('');
	const [passwordValue, handlePasswordChange] = useState('');
	const [loginStatus, handleLoginAttempt] = useState('');
	const [catalogs, updateCatalogs] = useState([]);
	const [palettes, updatePalettes] = useState([]);

	const handleSubmit = async event => {
		event.preventDefault();
		handleLoginAttempt('');
		const newUser = { email: emailValue, password: passwordValue };
		const loginResponse = await userLogin(newUser);
		if (loginResponse.error) {
			handleLoginAttempt(loginResponse.error, retrievedCatalogs);
		} else {
			console.log(updateCurrentUser);
			updateCurrentUser(loginResponse);
			resetInputs();
		}
		const retrievedCatalogs = await getCatalogs(loginResponse);
		const retrievedPalettes = await getPalettes();
	};

	const resetInputs = () => {
		handleEmailChange('');
		handlePasswordChange('');
		handleLoginAttempt('');
	};

	const checkEmail = () => {
		const emailSplit = emailValue.split('');
		const startIncludesSearch = emailSplit.findIndex(
			character => character === '@'
		);
		return emailSplit.includes('@') &&
			emailSplit.includes('.', startIncludesSearch)
			? true
			: false;
	};

	const canBeSubmitted = () => {
		return checkEmail() && passwordValue.length > 6;
	};

	const isEnabled = canBeSubmitted();

	return (
		<form className='Form'>
			<label htmlFor='email' className='form-login email-login__label'>
				Email
			</label>
			<input
				className='emailForm'
				id='email'
				type='text'
				name='email'
				placeholder='Email'
				onChange={e => handleEmailChange(e.target.value)}
				value={emailValue}
			/>
			<label htmlFor='password' className='form-login password-login__label'>
				Password
			</label>
			<input
				className='emailForm'
				id='password'
				type='password'
				name='password'
				placeholder='Password'
				onChange={e => handlePasswordChange(e.target.value)}
				value={passwordValue}
			/>
			<button
				type='button'
				className='loginFormBtn'
				disabled={!isEnabled}
				onClick={e => handleSubmit(e)}>
				Submit
			</button>
		</form>
	);
};

export default LoginForm;
